INSERT INTO recipes VALUES (1,'Homemade Chicken Alfredo',1,1196,50,14,105,35,'Dinner',2);
INSERT INTO recipe_instruction VALUES (1,1,'Wash Chicken breast then pat dry. Cut into thin strips. ',1),(2,1,'Boil fettucine pasta according to box instructions, when ready se aside.',2),(3,1,'Heat a non-stick pan with olive oil and add chicken strips. Cook for 6-7 minutes on each side until golden brown, on medium heat. Remove chicken from pan when ready and set aside.',3),(4,1,'Add minced garlic and saute for 3 minutes. Deglaze pan with chicken stock, add lemon juice and bring to boil.',4),(5,1,'Add heavy cream, then add the cooked pasta and chicken.',5),(6,1,'Add parmesan and stir until everything is well combined.',6),(7,1,'Sprinkle with parsley and enjoy!',7);
